from flask import Flask, jsonify, render_template, url_for, requestfrom flask_restful import Resource, Apifrom wtforms import StringField, SubmitFieldfrom flask_wtf import FlaskFormfrom weatherdata import *app = Flask(__name__)api = Api(app)app.config['SECRET_KEY'] = "secret"#Option1 Basic RestAPI using Flask@app.route('/', methods=['GET', 'POST'])def index():    if(request.method == 'POST'):        some_json = request.get_json()        return jsonify({'you sent':some_json}), 201    else:        return jsonify({'you get':'This is your API'})@app.route('/multi/<int:num>', methods=['GET'])def get_multiply10(num):    return jsonify({'result': num*10})#Option2 RestAPI using Flask-RestFulclass test_restfulAPI(Resource):    def get(self):        return {'you get restfulAPI': 'Successfully'}        def post(self):        some_json = request.get_json()        return {'you sent': some_json}, 201    class multi_restfulAPI(Resource):    def get(self, num):        return {'result': num*10}        def post(self, name, age):        return {'you sent': {name, age}}# A demo to get the API Weather for fun@app.route('/weather', methods=["POST", "GET"])def weather_index():    w = Weather()    form = Form()    data = w.the_weather_get(name='Ho Chi Minh')    if request.method == "POST":        if form.validate_on_submit():            txt_search = form.search.data            data = w.the_weather_get(name=txt_search)            return render_template("home.html", data=data, form=form)    else:        return render_template("home.html", data=data, form=form)class Form(FlaskForm):    button = SubmitField('Search')    search = StringField('submit')api.add_resource(test_restfulAPI, '/restfulAPI')api.add_resource(multi_restfulAPI, '/restfulAPI/multi/<int:num>')    if __name__ == '__main__':    app.run(debug=True, host='0.0.0.0')